&lt;button&gt;&gt;&gt;&lt;/button&gt;

&lt;div&gt;
    &lt;ul&gt;
        &lt;li&gt;&lt;img src="someimage" alt="" width="100" height="100" &gt;&lt;/li&gt;
        &lt;li&gt;&lt;img src="someimage" alt="" width="100" height="100" &gt;&lt;/li&gt;
