            //设置cookie
            ssetcookie('auth', authcode(&quot;$setarr[password]\t$setarr[uid]&quot;, 'ENCODE'), 2592000);
            ssetcookie('loginuser', $username, 31536000);
            
            showmessage('registered', rawurldecode($refer));
        }
